import React, { Component } from 'react';
import '../../css/Home.css';
import { Client, Team } from 'espn-fantasy-football-api/node';
import Table from 'react-bootstrap/Table';
import Card from 'react-bootstrap/Card';

const SID = '2020';
const LID = '434534';

const ffClient = new Client({ leagueId: LID });
const team = new Team({ });

class SideStandings extends Component {

    getStandings() {

        ffClient.getTeamsAtWeek({ seasonId: SID, scoringPeriodId: 18 }).then(response => {
            console.log({ response });
            // do stuff with data
        }).catch(error => {
            console.log({ error });
            // what went wrong
        })

    }

    render() {
        return (
            <div className="sidecard">
                <Card variant="dark">
                    <Card.Header>League Standings</Card.Header>
                    <Table>
                        <thead>
                            <tr>Team</tr>
                            <tr>W</tr>
                            <tr>L</tr>
                        </thead>
                    </Table>
                </Card>
                {this.getStandings()}
            </div>
        )
    }
}

export default SideStandings;
