import React, { useState } from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import '../../css/CharlieFormula.css';

const CreatePost = (props) => {

    const [create, setCreate] = useState(false);

    const chooseDisplay = () => {
        if(create) {
            return(
                <div className="formWrapper">
                    <Form.Group>
                        <Form.Label>Title</Form.Label>
                        <Form.Control className="titleForm" type="text" placeholder="Enter post title"/>
                        <Form.Label>Body</Form.Label>
                        <Form.Control className="bodyForm" as="textarea" rows={10}/>
                        <Button className="submitButton">Submit</Button>
                        <Button className="cancelButton" variant="secondary" onClick={() => setCreate(false)}>
                            Cancel
                        </Button>
                    </Form.Group>
                </div>
            )
        }
        else {
            return <Button onClick={() => setCreate(true)}>Create Post</Button>
        }
    }

    return (
        <div className="createPostWrapper">
            {chooseDisplay()}
        </div>
    )
}

export default CreatePost;
