import React, { Component } from 'react';
import Table from 'react-bootstrap/Table';
import Card from 'react-bootstrap/Card';
import StandingEntry from './StandingEntry.js';
import '../../css/Home.css';
import { Client, Team } from 'espn-fantasy-football-api/node';

const SID = '2020';
const LID = '434534';

const ffClient = new Client({ leagueId: LID });

class SideStandings extends Component {

    constructor(props) {
        super(props);

        this.state = {
            data: ''
        }
    }

    findOrder(data) {
        let ordered = [];

        for(const team in Object.entries(data)) {
            ordered[data[team].playoffSeed - 1] = data[team];
        }

        return ordered;
    }

    getStandings() {
        let standings = [];
        let mapping = [];

        ffClient.getTeamsAtWeek({ seasonId: SID, scoringPeriodId: 18 })
        .then(response => {
            standings = this.findOrder(response);
            this.setState({data: standings});
            for(let i = 0; i < standings.length; i++) {
                mapping.push(
                    <StandingEntry team={standings[i]} />
                )
            }
        })
        .catch(error => {
            console.log({ error });
        })


    }

    // react doesn't like async functions being called from render :(
    // async getData() {
    //     const response = await ffClient.getTeamsAtWeek({ seasonId: SID, scoringPeriodId: 18 });
        
    //     this.setState({data: response});
    // }

    // getOrder(data) {
    //     let ordered = [];

    //     for(const team in Object.entries(data)) {
    //         ordered[data[team].playoffSeed - 1] = data[team];
    //     }

    //     return ordered;
    // }

    // getStandings() {
    //     this.getData();
    //     //console.log(this.state.data);
    // }

    render() {
        return (
            <div className="sidecard">
                <Card>
                    <Card.Header>League Standings</Card.Header>
                    <Card.Body>
                        <Table size="sm">
                            <thead>
                                <th>Team</th>
                                <th>W</th>
                                <th>L</th>
                            </thead>
                            <tbody>
                                {this.getStandings()}
                            </tbody>
                        </Table>
                    </Card.Body>
                </Card>
            </div>
        )
    }
}

export default SideStandings;
