import React, { Component } from 'react';
import Table from 'react-bootstrap/Table';
import Card from 'react-bootstrap/Card';
import StandingEntry from './StandingEntry.js';
import '../../css/Home.css';
import { Client, Team } from 'espn-fantasy-football-api/node';

const LID = '434534';
const ffClient = new Client({ leagueId: LID });

class SideStandings extends Component {

    constructor(props) {
        super(props);

        this.state = {
            data: '',
            SID: '2020',
        }
    }

    componentDidMount() {
        this.getData();
    }

    async getData() {
        const response = await ffClient.getTeamsAtWeek({ seasonId: this.state.SID, scoringPeriodId: 18 });
        
        this.setState({data: response});
    }

    setOrder() {
        let ordered = [];

        for(const team in Object.entries(this.state.data)) {
            ordered[this.state.data[team].playoffSeed - 1] = this.state.data[team];
        }

        return ordered;
    }

    displayStandings() {
        let standings = [];
        let ordered = this.setOrder();
        for(let i = 0; i < ordered.length; i++) {
            standings.push(
                <StandingEntry name={ordered[i].name}
                    wins={ordered[i].wins}
                    losses={ordered[i].losses}/>
            )
        }

        return standings;
    }

    render() {
        return (
            <div className="standings">
                <Table size="sm" striped>
                            <thead>
                                <th className="leftalign">Team</th>
                                <th className="leftalign">W</th>
                                <th className="leftalign">L</th>
                            </thead>
                            <tbody className="leftalign">
                                {this.displayStandings()}
                            </tbody>
                </Table>
            </div>
        )
    }
}

export default SideStandings;
