import React, { useEffect } from 'react';
import '../../css/Teams.css';

const TeamPreview = (props) => {

    // useEffect(() => {
    //     formatPoints();
    // });

    const formatPoints = (points) => {
        console.log(points)
        console.log(points.toString().indexOf('.'));

    }
    
    return (
        <div className="previewRow">
            <img className="logo" src={props.logoURL} />
            <div className="description">
                <text className="teamName">{props.name}</text>
                <text className="w-l"> ({props.wins} - {props.losses})</text>
                <text>Points for: {formatPoints(props.pf)}</text>
                <text>Points against: {formatPoints(props.pa)}</text>
            </div>
        </div>
    )
}

export default TeamPreview;
