import React, { Component } from 'react';
import Table from 'react-bootstrap/Table';
import Card from 'react-bootstrap/Card';
import StandingEntry from './StandingEntry.js';
import '../../css/Home.css';
import { Client, Team } from 'espn-fantasy-football-api/node';

const SID = '2020';
const LID = '434534';

const ffClient = new Client({ leagueId: LID });

class SideStandings extends Component {

    constructor(props) {
        super(props);
        this.state = {
            data: ''
        }
    }

    getStandings() {

        ffClient.getTeamsAtWeek({ seasonId: SID, scoringPeriodId: 18 }).then(response => {
            //console.log(response);
            //this.setState({data: response});
        }).catch(error => {
            console.log({ error });
        });
    }

    render() {
        return (
            <div className="sidecard">
                <Card>
                    <Card.Header>League Standings</Card.Header>
                    <Card.Body>
                        <Table size="sm">
                            <thead>
                                <th>Team</th>
                                <th>W</th>
                                <th>L</th>
                            </thead>
                            <tbody>
                                {this.getStandings()}
                            </tbody>
                        </Table>
                    </Card.Body>
                </Card>
            </div>
        )
    }
}

export default SideStandings;
