import React, {Component} from 'react';
import '../css/ContentHandler.css';
import Home from './home/Home.js';
import TunaNavBar from './TunaNavBar.js';
import TunaTimes from './TunaTimes.js';
import CharlieFormula from './CharlieFormula.js';
import SideStandings from './home/SideStandings';

class ContentHandler extends Component {
    constructor(props) {
        super(props);
        this.state = {
            currPage: 'home'
        }

        this.goHome = this.goHome.bind(this);
        this.goTimes = this.goTimes.bind(this);
        this.goCharlie = this.goCharlie.bind(this);
    }

    goHome() {
        this.setState({currPage: 'home'});
    }
    goTimes() {
        this.setState({currPage: 'times'});
    }
    goCharlie() {
        this.setState({currPage: 'charlie'});
    }

    // chooses which page to render
    pagePicker() {
        if(this.state.currPage == 'home') {
            return(
                <div className="row">
                    <div className="col1">
                        <Home />
                    </div>
                    <div className="col2">
                        <SideStandings />
                    </div>
                </div>
            )
        }
        if(this.state.currPage == 'times') {
            return(
                <TunaTimes/>
            )
        }
        if(this.state.currPage == 'charlie') {
            return(
                <CharlieFormula/>
            )
        }
    }

    render() {
        return(
            <div className='Wrapper'>
                <TunaNavBar goHome={this.goHome}
                            goTimes={this.goTimes}
                            goCharlie={this.goCharlie}/>
                <div>
                    {this.pagePicker()}
                </div> 
            </div>
        );
    }
}

export default ContentHandler;
