import React, { Component } from 'react';
import Table from 'react-bootstrap/Table';
import Card from 'react-bootstrap/Card';
import StandingEntry from './StandingEntry.js';
import '../../css/Home.css';
import { Client, Team } from 'espn-fantasy-football-api/node';

const SID = '2020';
const LID = '434534';

const ffClient = new Client({ leagueId: LID });
const team = new Team({ });

class SideStandings extends Component {

    getStandings() {

        ffClient.getTeamsAtWeek({ seasonId: SID, scoringPeriodId: 18 }).then(response => {
            console.log({ response });
            // do stuff with data
        }).catch(error => {
            console.log({ error });
            // what went wrong
        })
    }

    render() {
        return (
            <div className="sidecard">
                <Card>
                    <Card.Header>League Standings</Card.Header>
                    <Card.Body>
                        <Table size="sm">
                            <thead>
                                <th>Team</th>
                                <th>W</th>
                                <th>L</th>
                            </thead>
                            <tbody>
                                <StandingEntry name={"team name"} wins={16} losses={0}/>
                                <StandingEntry name={"okayaysdf name"} wins={16} losses={0} />
                                <StandingEntry name={"team name"} wins={16} losses={0} />
                                <StandingEntry name={"team name"} wins={16} losses={0} />
                            </tbody>
                        </Table>
                    </Card.Body>
                </Card>
                {this.getStandings()}
            </div>
        )
    }
}

export default SideStandings;
