const ContentHandler = () => {

    const [page, setPage] = useState('landing');

    const goLanding = () => {
        setPage('landing');
    }
    const goHome = () => {
        setPage('home');
    }
    const goTimes = () => {
        setPage('times');
    }
    const goCharlie = () => {
        setPage('charlie');
    }
    const goMembers = () => {
        setPage('members');
    }

    const showNavBar = () => {
        if (page != 'landing') {
            return (
                <TunaNavBar goHome={goHome}
                    goTimes={goTimes}
                    goCharlie={goCharlie}
                    goMembers={goMembers}
                    goLanding={goLanding} />
            )
        }
    }

    // chooses which page to render
    const pagePicker = () => {
        if (page == 'landing') {
            return (
                <Landing goHome={goHome} />
            )
        }
        if (page == 'home') {
            return (
                <Home />
            )
        }
        if (page == 'times') {
            return (
                <TunaTimes />
            )
        }
        if (page == 'charlie') {
            return (
                <CharlieFormula />
            )
        }
        if (page == 'members') {
            return (
                <MembersPage />
            )
        }
    }
    return (
        <div className='Wrapper'>
            {showNavBar()}
            <div className="pageWrapper">
                {pagePicker()}
            </div>
        </div>
    );
}

export default ContentHandler;
