import React, { Component } from 'react';
import '../../css/Home.css';
import { Client, Team } from 'espn-fantasy-football-api/node';

const ffClient = new Client({ leagueId: 434534});
const team = new Team();

const SID = 2020;
const ESPNS2 = 'AEA8PxwpDjxeSGbp6mpZGDR2rPfkWbbwQZohFe7GvqdrS7yxbexL1y1L9F9ZmjOsYI0ivsIS3YLkI980hH4bN%2BdCS5%2BlBdZCUEZ7VV7yGGv3uI%2FWnJRfeVo6MNwG2pk%2BfHgngwe3VPBsa2M6NQv4Ki1peNQijtacIZiVUu0JJrRrvBDz%2Fk6ak59Jxo5gpzh%2FGeQreSD%2Bed%2FeUC7NTK5UWcm2WvfQm2qqxR2XY5q%2FMUqcn4uiqHYTUPXHLHeFuCZXe%2FxhyeBOlxFGuRXu%2FulFYOio';
const SWID = '{DC03AED4-FCA6-4FC2-83AE-D4FCA68FC233}';

ffClient.setCookies({ espnS2: ESPNS2,
    SWID: SWID});



class SideStandings extends Component {

    getStandings() {
        ffClient.getLeagueInfo({ seasonId: SID }).then(response => {
            console.log({ response });
            // do stuff with data
        }).catch(error => {
            console.log({ error });
            // what went wrong
        })
    }

    getFAs() {
        ffClient.getFreeAgents({ seasonId: SID, scoringPeriodId: 0 }).then(response => {
            // console.log({response});

            // This will give a list of all free agent players (VERY LONG)

            // for (player in response) {
            //     console.log(response[player]);
            // }

        }).catch(error => {
            console.log({ error });
            // what went wrong
        })
    }

    render() {
        return (
            <div className="sidebar">
                side standings ayyyy
                {/* {this.getStandings()} */}
                {this.getFAs()}
            </div>
        )
    }
}

export default SideStandings;
